select 

from REP_REVANUE_MONTHLY


    and TURNOVER = #{turnover,jdbcType=DECIMAL}


    and ORDER_NUMBER = #{orderNumber,jdbcType=BIGINT}


    and PROFIT = #{profit,jdbcType=DECIMAL}


    and COST = #{cost,jdbcType=DECIMAL}


    and MONTH_OF_YEAR = #{monthOfYear,jdbcType=INTEGER}


    and YEAR = #{year,jdbcType=INTEGER}


    and MODIFIED_TIMESTAMP = #{modifiedTimestamp,jdbcType=BIGINT}


select count(*) 
from REP_REVANUE_MONTHLY
where ID = #{id,jdbcType=BIGINT}


